INSERT INTO `rest`.`invoke_info` (`id`, `systemId`, `name`, `descrption`, `method`, `url`, `head`, `body`, `parseFun`, `orginalResult`, `next`, `invokeType`, `groupName`, `enableLog`) VALUES (2, '1', 'mock_province', '', 'POST', '', '{\n  \"Accept\":\"application/json\",\n  \"Content-Type\":\"application/json;charset=UTF-8\"\n}', '{}', 'function paraphraseFun(obj){\n  let data =  this.defaultValue(obj)\n  for (let p of data) {\n    delete p.children\n  }\n  return data\n}', NULL, '1', '2', 'mock', '');
INSERT INTO `rest`.`invoke_info` (`id`, `systemId`, `name`, `descrption`, `method`, `url`, `head`, `body`, `parseFun`, `orginalResult`, `next`, `invokeType`, `groupName`, `enableLog`) VALUES (3, '1', 'mock_city', '', 'POST', '', '{\n  \"Accept\":\"application/json\",\n  \"Content-Type\":\"application/json;charset=UTF-8\"\n}', '{}', 'function paraphraseFun(obj,requestBody){\n  let data =  this.defaultValue(obj)\n  const {provinceId} = requestBody\n  const result =  data.filter(p=> p.id== provinceId).map( p=> p.children).reduce((a,b)=> a.concat(b))\n  for (let p of result) {\n    delete p.children\n  }\n  return result\n}', NULL, '1', '2', 'mock', '');
INSERT INTO `rest`.`invoke_info` (`id`, `systemId`, `name`, `descrption`, `method`, `url`, `head`, `body`, `parseFun`, `orginalResult`, `next`, `invokeType`, `groupName`, `enableLog`) VALUES (4, '1', 'mock_district', '', 'POST', '', '{\n  \"Accept\":\"application/json\",\n  \"Content-Type\":\"application/json;charset=UTF-8\"\n}', '{}', 'function paraphraseFun(obj,requestBody){\n  let data =  this.defaultValue(obj)\n  const {cityId} = requestBody\n  const result =  data.map( p=> p.children).reduce((a,b)=> a.concat(b))\n    .filter( c => c.id == cityId).map(c => c.children).reduce((a,b)=> a.concat(b))\n  for (let p of result) {\n    delete p.children\n  }\n  return result\n}', NULL, '1', '2', 'mock', '');
