INSERT INTO `rest`.`invoke_info` (`id`, `systemId`, `name`, `descrption`, `method`, `url`, `head`, `body`, `parseFun`, `orginalResult`, `next`, `invokeType`, `groupName`, `enableLog`) VALUES (8, '2', 'assemble_all', NULL, 'POST', NULL, '{\n  \"Accept\":\"application/json\",\n  \"Content-Type\":\"application/json;charset=UTF-8\"\n}', '{}', 'function paraphraseFun(obj,requestBody){\n  const provinceList = this.keyList(obj,\'assemble_province\')\n  const cityList = this.keyList(obj,\'assemble_city\')\n  const districtList = this.keyList(obj,\'assemble_district\')\n  return provinceList.map(p=>{\n    const citys = cityList.filter(c=>c.parentId == p.id)\n    return {\n      ...p,\n      children: citys.map(c=>{\n        const districts = districtList.filter(d=> d.parentId == c.id )\n        return {\n          ...c,\n          children: districts\n        }\n      })\n    }\n  })\n}', NULL, '5', '2', 'assemble_example', NULL);
INSERT INTO `rest`.`invoke_info` (`id`, `systemId`, `name`, `descrption`, `method`, `url`, `head`, `body`, `parseFun`, `orginalResult`, `next`, `invokeType`, `groupName`, `enableLog`) VALUES (9, '2', 'transform_city', 'get cites with provinceId', 'POST', '@baseUrl/invoke/mock_city', '{\n  \"Accept\":\"application/json\",\n  \"Content-Type\":\"application/json;charset=UTF-8\",\n  \"logKey\":\"@requestField\"\n}', '{\n  \"provinceId\":\"@provinceId\",\n  \"requestField\":\"@requestField\"\n}', 'function paraphraseFun(resObj, resHead, resStatus, reqHead, reqBody, url){\n  if(resStatus != 200){\n    return {\n      success: false,\n      error: \'network error!!\'\n    }\n  }\n  const {requestField} = reqBody\n  return {\n    success: true,\n    data: resObj.map(o=>{\n      return {\n        cityId:o.id,\n        pid:o.parentId,\n        cityName:o.name,\n        newField: requestField\n      }\n    })\n  }\n}', NULL, NULL, '1', 'transform_example', NULL);
INSERT INTO `rest`.`invoke_info` (`id`, `systemId`, `name`, `descrption`, `method`, `url`, `head`, `body`, `parseFun`, `orginalResult`, `next`, `invokeType`, `groupName`, `enableLog`) VALUES (10, '2', 'transform_district', 'get cites with provinceId', 'POST', '@baseUrl/invoke/mock_district', '{\n  \"Accept\":\"application/json\",\n  \"Content-Type\":\"application/json;charset=UTF-8\"\n}', '{\n  \"cityId\":\"@cityId\"\n}', 'function paraphraseFun(resObj, resHead, resStatus, reqHead, reqBody, url){\n  if(resStatus != 200){\n    return {\n      success: false,\n      error: \'network error!!\'\n    }\n  }\n  const {requestField} = reqBody\n  return {\n    code: resStatus,\n    data: resObj.map(o=>{\n      return {\n        id:o.id,\n        name:o.name,\n        requestUrl:url\n      }\n    })\n  }\n}', NULL, NULL, '1', 'transform_example', NULL);
